-- Table structure for consultant_attach
-- ----------------------------
DROP TABLE IF EXISTS `consultant_attach`;
CREATE TABLE `consultant_attach`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '编号',
  `consultant_id` int NOT NULL DEFAULT 0 COMMENT '咨询师id',
  `image_type` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '图片类型（idCard1：身份证正面，idCard2：身份证反面，credential：相关证书）',
  `image_url` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '图片路径',
  `image_name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '图片名称',
  `create_time` timestamp(0) NOT NULL DEFAULT CURRENT_TIMESTAMP(0) COMMENT '创建时间',
  `update_time` timestamp(0) NOT NULL DEFAULT CURRENT_TIMESTAMP(0) ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '更新时间',
  `is_deleted` tinyint(1) NOT NULL DEFAULT 0 COMMENT '逻辑删除(1:已删除，0:未删除)',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `consultant_id`(`consultant_id`) USING BTREE,
  CONSTRAINT `consultant_attach_ibfk_1` FOREIGN KEY (`consultant_id`) REFERENCES `consultant` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 7 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '咨询师身份验证表' ROW_FORMAT = Dynamic;

