DROP TABLE IF EXISTS `consulting_order`;
CREATE TABLE `consulting_order`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '编号',
  `user_id` int NOT NULL COMMENT '用户id',
  `consultant_id` int NOT NULL COMMENT '咨询师id',
  `amount` int NOT NULL COMMENT '金额',
  `status` int NOT NULL COMMENT '订单状态(0 已支付待咨询师确认；1 咨询进行中；2 用户确定已完成；3 已取消)',
  `is_deleted` tinyint(1) NOT NULL DEFAULT 0 COMMENT '逻辑删除(1:已删除，0:未删除)',
  `create_time` timestamp(0) NOT NULL DEFAULT CURRENT_TIMESTAMP(0) COMMENT '创建时间',
  `update_time` timestamp(0) NOT NULL DEFAULT CURRENT_TIMESTAMP(0) ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `user_id`(`user_id`) USING BTREE,
  INDEX `consultant_id`(`consultant_id`) USING BTREE,
  CONSTRAINT `consulting_order_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `consulting_order_ibfk_2` FOREIGN KEY (`consultant_id`) REFERENCES `consultant` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 7 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '订单表' ROW_FORMAT = Dynamic;

