DROP TABLE IF EXISTS `questionnaire_item`;
CREATE TABLE `questionnaire_item`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '编号',
  `questionnaire_id` int NOT NULL COMMENT '问卷id',
  `user_id` int NOT NULL COMMENT '用户id',
  `author_id` int NULL DEFAULT NULL COMMENT '作者id',
  `score` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '分数/结果',
  `result` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '结果说明',
